import { createSlice } from "@reduxjs/toolkit";
// import products from "../components/data";
import { products } from './../components/data';

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: products,
    totalAmount: 0,
    totalCount: 0,
  },
  reducers: {},
});

export default cartSlice.reducer;
