import { createSlice } from "@reduxjs/toolkit";
import { data } from "./../components/data";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: data,
    totalAmount: 0,
    totalCount: 0,
    cart:[]
  },
  reducers: {
    setIncrement: (state, { payLoad }) => {
      state.totalAmount = payLoad;
    },

    addToCart: (state,) => {},
  },
});

export default cartSlice.reducer;
